settings.custom_css:
a.button.wc-backward {

    background-color: #E6C8C3 !important;
    color:#484F5B !important;
    font-weight: 900 !important;
    padding:15px 30px 15px 30px !important;
    border-radius: 0px !important;
}

a.button.wc-backward:hover {

    background-color: #E6C8C3;
    color:#484F5B;
    font-weight: 900;
    padding:15px 30px 15px 30px;
    border-radius: 0px;
}


p.cart-empty.woocommerce-info {
    border-top-color:#E6C8C3;
    background-color: #ffffff;
    color:#484F5B;
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);


}
p.cart-empty.woocommerce-info::before {
    color: #484f5b;
}

table.shop_table {
        border-top-color: #E6C8C3;
    background-color: #ffffff;
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);

}

table,thead, tbody, tr, th, td { 
    border:none;

}
thead, th {
    background-color: rgba(0, 0, 0, 0.03);
    height: 57px;
}


th {
    color:#484F5B;
}

.attachment-woocommerce_thumbnail.size-woocommerce_thumbnail {
    width:128px !important;

}

.product-name > a {
    color:#E6C8C3;
}

.quantity.buttons-added {
    display:inline-flex;
}

.remove {
    width: 25px !important;
    height: 25px !important;
    line-height: 19px !important;
}

div.coupon > button.button {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
    font-weight: 900;
    height: 57px;
}

div.coupon > button.button:hover {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
    height: 57px;
}

input#coupon_code.input-text {
    width: auto !important;
    height: 57px;
    padding: 10px 15px 10px 15px;
}

td.actions > button.button {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
    font-weight: 900;
    height: 57px;

}

td.actions > button.button:hover {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/

}

.woocommerce-remove-coupon {
    color:#E6C8C3;
}

.woocommerce-remove-coupon:hover {
    color:#E6C8C3;
}

.checkout-button.button.alt.wc-forward {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;

    font-weight: 900;


}

.checkout-button.button.alt.wc-forward:hover {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;

    font-weight: 900;

}

div.woocommerce-notices-wrapper {
    /*display:none;*/
}
.woocommerce-message, .woocommerce-info {
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);
    background-color: #484F5B !important;
    color:white !important;
}

.woocommerce table.shop_table {
    background-color: white;
}
@media only screen and (max-width: 768px) {
.woocommerce-page table.cart td.actions .coupon .input-text+.button {

    display: block !important;
    width: 100% !important;
    font-size:17px;
}
input#coupon_code.input-text {
    width: 100% !important;

}
}
