settings.custom_css:
a {
    color:#E6C8C3;
}

th {
    color:#484F5B;
}

td.product-name, .product-name > a {
    color:#E6C8C3;
}

.woocommerce button.button {
    background-color: #E6C8C3 !important;
    color:#484F5B !important;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
    font-weight: 900 !important;


}

.woocommerce button.button:hover {
    background-color: #E6C8C3;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
}

#coupon_code.input-text {
    width: auto !important;
    /*padding: 10px 15px 10px 15px;*/
    height: 57px;
}
.checkout_coupon {
    background-color: #ffffff;
    border-radius: 0px;
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);
    padding: 20px !important;
}

.form-row.form-row-last > .button {
    background-color: #E6C8C3 !important;
    color:#484F5B !important;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/
    font-weight: 900;
    height: 57px;
    font-size: 15px;
}

.showcoupon, .showlogin {
    color:#E6C8C3 !important;
}

.lost_password > a {
    color:#E6C8C3;
}

.woocommerce-form-login .woocommerce-form-login__submit {
    background-color: #484F5B !important;
    font-weight: 900;
}

.woocommerce-message, .woocommerce-info {
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);
    background-color: #484F5B !important;
    color:white !important;
}

