.woocommerce-info::before {
    color: #ffffff;
}

.woocommerce-info, .woocommerce-message {
    border-top-color: #484f5b;
}

div.woocommerce-info > a.showcoupon {
    color:#E6C8C3;
}


button#place_order.button.alt, a.button.wc-forward {
    background-color: #E6C8C3 !important;
    color:#484F5B;
    border-radius: 0px;
    /*padding:15px 30px 15px 30px;*/

}

.checkout.woocommerce-checkout {
    background-color: white;
    padding: 20px;
    border-radius: 0px;
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.12);

}

.woocommerce .col2-set, .woocommerce-page .col2-set {

    margin-bottom: 60px;
}

.woocommerce-checkout #payment {
    background: transparent;
    border-radius: 0px;
}

.woocommerce-checkout #payment div.payment_box {
    border:3px solid #E6C8C3;
    border-radius: 0px;
    background-color: #ffffff;
    color: #484F5B;
    padding:30px;
}

.woocommerce-checkout #payment div.payment_box::before {

    border: 1em solid #E6C8C3;
    border-right-color: transparent;
    border-left-color: transparent;
    border-top-color: transparent;
    position: absolute;
    top: -.99em;
    left: 0;
    margin: -1em 0 0 2em;
}

/*form inputs*/
 .select2-container--default .select2-selection--single {
    height: auto !important;
    padding: 10px !important;
}

  .select2-container--default .select2-selection--single .select2-selection__arrow {
    height: 46px;   
    width: 30px;
}
  /*Hide additional notes*/
  .woocommerce-additional-fields {
    display:none;
  }

   /*Inputs validated border style*/

  .woocommerce-validated .input-text {

    /*border-radius: 6px;*/
    border: 3px solid #484F5B !important;


  }

  .woocommerce-validated .select2-container,  .woocommerce-validated select .select2-container--default .select2-selection--single {
    /*border-radius: 6px;*/
    border: 2px solid #484F5B !important;
    padding-bottom: 4px;
  }



/*form Input bordesr*/
input[type="text"], input[type="email"], input[type="url"], input[type="password"], input[type="search"], input[type="tel"], input[type="number"], textarea, select,
.select2-container--default .select2-selection--single {
    background-color: #fff;
    border: 1px solid #484F5B;
    border-radius: 0px;
    height: 57px !important;
}

.select2-container--default .select2-selection--single .select2-selection__arrow {
    height: 57px;


}

.select2-container--default .select2-selection--single .select2-selection__rendered {
    color: #444;
    line-height: 36px;
    /*height: 57px;*/

}

.select2-container--default .select2-results__option--highlighted[aria-selected], .select2-container--default .select2-results__option--highlighted[data-selected] {
    background-color: #484F5b;
    color: #fff;
}

.select2-container--default .select2-results__option[data-selected=true] {
    background-color: #E6C8C3;
    color:#484F5b;
}

.required {
    color: #E6C8C3 !important;

}

/*form label style*/
.woocommerce form .form-row label {
    line-height: 2;
    color: #484F5b;
    font-weight: 600;
}


.wc-stripe-elements-field {
    height:57px !important;
    padding:20px;
    border: 1px solid #484F5B;
}

.woocommerce-privacy-policy-link, .woocommerce-terms-and-conditions-link {
    color:#E6C8C3 !important;
}

#stripe-payment-data > p > a {
    color:#E6C8C3 !important;
}

.woocommerce-checkout #payment div.payment_box {
    font-size: 14px;
}
