settings.custom_css:
a {
    color:#E6C8C3;
}

a, a:visited {
    color: #E6C8C3;
}

th {
    color:#484F5B;
}

td.product-name > a {
    color:#E6C8C3;
}

.woocommerce button.button, .button {
    background-color: #E6C8C3 !important;
    color:#484F5B !important;
    border-radius: 0px;
    padding:15px 30px 15px 30px !important;
    font-weight: 900;

}





.woocommerce-info, .woocommerce-message {
    border-top-color:#E6C8C3 !important;
    background-color: #ffffff !important;
    color:#484F5B !important;
    box-shadow: 11px 11px 10px 0px rgba(0, 0, 0, 0.0) !important;


}
.woocommerce-info::before {
    color: #484f5b;
}

div.woocommerce-info > a.showcoupon {
    color:#E6C8C3;
}




/*form inputs*/
 .select2-container--default .select2-selection--single {
    height: auto !important;
    padding: 10px !important;
}

  .select2-container--default .select2-selection--single .select2-selection__arrow {
    height: 46px;   
    width: 30px;
}
  /*Hide additional notes*/
  div.woocommerce-additional-fields {
    display:none;
  }

   /*Inputs validated border style*/

  .woocommerce form .form-row.woocommerce-validated input.input-text {

    /*border-radius: 6px;*/
    border: 1px solid green !important;


  }

  .woocommerce form .form-row.woocommerce-validated .select2-container, .woocommerce form .form-row.woocommerce-validated select {
    /*border-radius: 6px;*/
    border: 1px solid green !important;
    padding-bottom: 4px;
  }



/*form Input bordesr*/
input[type="text"], input[type="email"], input[type="url"], input[type="password"], input[type="search"], input[type="tel"], input[type="number"], textarea, select,
.select2-container--default .select2-selection--single {
    background-color: #fff;
    border: 1px solid #484F5B;
    border-radius: 0px;
    height: 57px !important;
}

.select2-container--default .select2-selection--single .select2-selection__arrow {
    height: 57px !important;


}

.select2-container--default .select2-selection--single .select2-selection__rendered {
    color: #444 !important;
    line-height: 36px !important;
    /*height: 57px;*/

}

.select2-container--default .select2-results__option--highlighted[aria-selected], .select2-container--default .select2-results__option--highlighted[data-selected] {
    background-color: #484F5b !important;
    color: #fff !important;
}

.select2-container--default .select2-results__option[data-selected=true] {
    background-color: #E6C8C3 !important;
    color:#484F5b !important;
}

.required {
    color: #E6C8C3 !important;

}



/*form label style*/
label {
    line-height: 2;
    color: #484F5b;
    font-weight: 600;
}


a.edit {
        background-color: #484F5B !important;
    color: white;
    padding: 10px;
}
address {

    margin-top:20px;
}

img.avatar.avatar-160.photo {
    width: 120px;
    border-radius: 100%

}

/*Order details - marks*/

mark.order-number, mark.order-date, mark.order-status {
    color:#484F5B;
    background-color: #E6C8C3;
    padding: 5px;
    font-weight: 900;
}

/*custom Tabs*/

a.wshkcustomtab {
background-color:#fff;
padding:30px;
width:100%;
color:#484F5B;
font-weight:700;
font-size:20px;
text-align:center;
display:block;
border-radius:3px;
border:1px solid white;
}

a.wshkcustomtab:hover, a.wshkcustomtab:focus {
    border:1px solid #E6C8C3;
    border-radius:3px;
}

i.fa {
font-size:64px;
color:#E6C8C3;
}

@media screen and (max-width: 998px) and (min-width: 768px) {
    a.wshkcustomtab {
background-color:#fff;
padding:15px;
width:100%;
color:#484F5B;
font-weight:700;
font-size:13px;
text-align:center;
display:block;
border-radius:3px;
}

i.fa {
font-size:48px;
color:#E6C8C3;
}

}
table.shop_table {
    border-bottom: 1px solid rgba(0,0,0,.1) !important;

}
.shop_table.shop_table_responsive.my_account_orders th {
    background-color: rgba(0,0,0,.03);
    padding: 20px;
    border: none;

}

table, th, td {
    border: none;
}

th.woocommerce-table__product-name.product-name, th.woocommerce-table__product-table.product-total, th.payment-method-method, th.payment-method-expires, th.payment-method-actions {
    background-color: rgba(0,0,0,.03);
    padding: 20px;
    border: none;

}
section.woocommerce-order-details {
    margin-top: 30px;
}

.wc-stripe-elements-field {
    height:57px !important;
    padding:20px;
    border: 1px solid #484F5B;
}

div#stripe-payment-data > p > a {
    color:#E6C8C3 !important;
}

@media only screen and (min-width: 769px) {
tr.order > td {
    padding: 10px;
    min-height: 60px !important;
    height: auto;
}
tr.payment-method > td {
    padding: 15px;
    min-height: 60px !important;
    height: auto;
}
tr.payment-method.default-payment-method:after{
    /*font-family: FontAwesome;
   content: '\f05d';*/
   content: 'Default';
    position: absolute;
    /* z-index: 1; */
    right: 50px;
    margin-top: 20px;
    margin-right: 50px;
    background-color: #484F5B;
    padding: 5px;
    border-radius: 3px;
    width: auto;
    height: auto;
    color: white;
    font-weight: 900;
    letter-spacing:1px;
}
}

